import React, { Component } from 'react';
import Player from "./Player";
import Search from "./Search";
import { connect } from "react-redux";
import { addToken } from "../js/actions/index";

$(document).ready(function () {
  $("#slider").slider(
  {
      min: 0,
      max: 100,
      step: 1,
      change: showValue

  });
  $("#update").click(function () {
      $("#slider").slider("option", "value", $("#seekTo").val());

  });
  function showValue(event, ui) {
      $("#val").html(ui.value);
  }
});

class Home extends Component {
  constructor() {
    super();

    this.state = {
      tokenValue: ""
    }
  }

  render () {
    return(
      <div className="app">
        <div>
                <a href='http://localhost:8888' class="text-white"> Login to Spotify </a>
        </div>
        <div>
          <div>
            <Player/>
          </div>
          <div>
            <Slider/>
          </div>
          <div>
            <Search/>
          </div>
        </div>
        <div id="slider"></div>
        Seek To : <input id="seekTo" type="text" value="10" />
        <input id="update" type="button" value="Update" />
        Current Value : <span id="val">0</span>
      </div>
    )
  }
}

function mapStateToProps(state) {
  return {
    token: state.token
  };
}

export default connect(
  mapStateToProps,
  { addToken }
)(Home);
