const spotifyApi = new SpotifyWebApi();


class Home extends Component() {
  constructor() {
    super();

    this.state = {
      tokenValue: ""
    }
  }

  render () {
    return(
      <div className="app">
        <div>
                <a href='http://localhost:8888' class="text-white bg-dark"> Login to Spotify </a>
