import React, { Component } from "react";
import { connect } from "react-redux";
import { addToken } from "../js/actions/index";
import $ from 'jquery';

var token;
var href;

export class Search extends Component {
  constructor() {
    super();
    this.state = {
      searchObject: [],
      query: "jude"
    }
    this.seeSong = this.seeSong.bind(this);
  }

  search(){
    token = "" || this.props.token[0]
    var query = prompt("Please enter track name") || this.state.query
    $.ajax({
      url: "https://api.spotify.com/v1/search?" + "q=name:" + `${query}` + "&type=track",
      headers: {
        'Authorization': `Bearer ${token}`,
      },
      type: "GET",
      contentType: JSON,
    })
    .then ((data) => {
      debugger
        this.setState({
            searchObject: data.tracks.items
        })
    })
}

seeSong(event){
  event.preventDefault();
  token = "" || this.props.token[0]
  var url=event.target[0].value
  $.ajax({
    url: url,
    headers: {
      'Authorization': `Bearer ${token}`,
    },
    type: "GET",
    contentType: JSON,
  })
  .then((data) => {
    debugger
  })
}



  render() {
    return (
      <div>
        <div>
          {this.props.token}
        </div>
        <div>
          <button onClick={() => this.search()}>
            search
          </button>
        </div>
        <div>
        <div class="row">
          {this.state.searchObject.map(object => (
            <div class="col-sm-3">
             <h3>
              <img src={object.album.images[0].url} style={{ height: 150 }}/>
              <br></br>
              Song Title: {object.name}
              <br></br>
              Artist: {object.artists[0].name}
              <br></br>
        <form onSubmit={this.seeSong}>
        <div className="form-seeSong">
          <label htmlFor="search">search</label>
          <input type="hidden" value={object.href} name="href" />
        </div>
        <button type="submit" className="btn btn-success btn-lg">
          SEE Song
        </button>
      </form>
            </h3>
            </div>
          ))}
        </div>
        </div>
      </div>

    );
  }
}
function mapStateToProps(state) {
  return {
    token: state.token
  };
}
export default connect(
  mapStateToProps,
  { addToken }
)(Search);
